mapper.select:
select  from adv where
(select id from adv

    and course_name like CONCAT('%', #{courseName}, '%') 
    and status_id = #{statusId}
    and is_putaway = #{isPutaway}
    and is_free = #{isFree}
    and audit_status = #{auditStatus}
    and audit_status != 1

order by id desc limit #{offset},1
) >= id
and course_name like CONCAT('%', #{courseName}, '%') 
and status_id = #{statusId}
and is_putaway = #{isPutaway}
and is_free = #{isFree}
and audit_status = #{auditStatus}
and audit_status != 1
order by id desc limit #{size}
